// Swiper.js„ÅÆ„Ç§„É≥„Éù„Éº„Éà
import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Autoplay, Navigation, Pagination } from 'swiper';  // SwiperCore„Å®ÂøÖË¶Å„Å™„É¢„Ç∏„É•„Éº„É´„Çí„Ç§„É≥„Éù„Éº„Éà
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import './SliderComponent.css'; // CSS„Éï„Ç°„Ç§„É´„Çí„Ç§„É≥„Éù„Éº„Éà

// Swiper„É¢„Ç∏„É•„Éº„É´„ÇíÁôªÈå≤
SwiperCore.use([Autoplay, Navigation, Pagination]);
function SliderComponent() {
  return (
    <div className="slider-container">
      <Swiper
        slidesPerView={1}        // ÂàùÊúüË°®Á§∫„ÅÆ„Çπ„É©„Ç§„ÉâÊï∞
        spaceBetween={24}        // „Çπ„É©„Ç§„ÉâÈñì„ÅÆ„Çπ„Éö„Éº„Çπ
        pagination={{ clickable: true }} // „Éö„Éº„Ç∏„Éç„Éº„Ç∑„Éß„É≥„Çí„ÇØ„É™„ÉÉ„ÇØÂèØËÉΩ„Å´
        navigation               // ÂâçÂæå„ÅÆ„Éä„Éì„Ç≤„Éº„Ç∑„Éß„É≥Áü¢Âç∞„ÇíÊúâÂäπÂåñ
        autoplay={{              // Ëá™Âãï„Çπ„É©„Ç§„Éâ
          delay: 2500,           // 2.5Áßí„Åî„Å®„Å´„Çπ„É©„Ç§„Éâ
          disableOnInteraction: false,
        }}
        loop={true}              // „Çπ„É©„Ç§„Éâ„Çí„É´„Éº„Éó„Åï„Åõ„Çã
        breakpoints={{           // „É¨„Çπ„Éù„É≥„Ç∑„ÉñÂØæÂøú„ÅÆË®≠ÂÆö
          600: {
            slidesPerView: 2,    // ÁîªÈù¢ÂπÖ600px‰ª•‰∏ä„Åß„ÅØ2„Çπ„É©„Ç§„ÉâË°®Á§∫
          },
          1025: {
            slidesPerView: 4,    // ÁîªÈù¢ÂπÖ1025px‰ª•‰∏ä„Åß„ÅØ4„Çπ„É©„Ç§„ÉâË°®Á§∫
            spaceBetween: 32,    // „Çπ„É©„Ç§„ÉâÈñì„ÅÆ„Çπ„Éö„Éº„Çπ„Çí32px„Å´Â§âÊõ¥
          },
        }}
      >
        {/* „Çπ„É©„Ç§„Éâ1 */}
        <SwiperSlide>
          <div className="slide-content">
            <img src={`${process.env.PUBLIC_URL}/slider1.png`} alt="Slider 1" />
            <h3>1ÊûöÁõÆ</h3>
            <p>Êõ∏„Åè</p>
          </div>
        </SwiperSlide>

        {/* „Çπ„É©„Ç§„Éâ2 */}
        <SwiperSlide>
          <div className="slide-content">
            <img src={`${process.env.PUBLIC_URL}/slider2.png`} alt="Slider 2" />
            <h3>2ÊûöÁõÆ</h3>
            <p>Êõ∏„Åè</p>
          </div>
        </SwiperSlide>

        {/* „Çπ„É©„Ç§„Éâ3 */}
        <SwiperSlide>
          <div className="slide-content">
            <img src={`${process.env.PUBLIC_URL}/slider3.png`} alt="Slider 3" />
            <h3>3ÊûöÁõÆ</h3>
            <p>Êõ∏„Åè</p>
          </div>
        </SwiperSlide>

        {/* „Çπ„É©„Ç§„Éâ4 */}
        <SwiperSlide>
          <div className="slide-content">
            <img src={`${process.env.PUBLIC_URL}/slider3.png`} alt="Slider 4" />
            <h3>4ÊûöÁõÆüòâ</h3>
            <p>„Çπ„É©„Ç§„Éâ„Åó„Å¶„Åè„Çå„Å¶„ÅÇ„Çä„Åå„Å®„ÅÜÔºÅ</p>
          </div>
        </SwiperSlide>

      </Swiper>
      <div class="swiper-button-prev"></div>
      <div class="swiper-button-next"></div>
    </div>
  );
}

export default SliderComponent;
